// This will be useful for the `let` and `match` forms, where we expect a list of pairs of nodes.
const makePairs = array => {
  const res = [];
  for (let i = 0; i < array.length - 1; i += 2) {
    res.push([array[i], array[i + 1]]);
  }
  return res
}

