const evaluateStmt = (node, env) => {
  switch (node.type) {
    case 'sexpr': return evaluate(node[0], env)
    case 'sdef':
      const value = evaluate(node[1], env)
      env[node[0]] = value
      return value
    case 'sdeftype':
      const res = {}
      unwrapList(node[1]).forEach(({0: name, 1: args}) => {
        res[name] = env[name] = constrFn(name, args)
      })
      return res
  }
}

